import { PLACE_BEAD, SET_BRUSH_POSITION } from '../constants/constants'

export function placeBead(payload){
    return {
        type: PLACE_BEAD,
        payload
    }
}

export function setBrushPosition(payload){
    return {
        type: SET_BRUSH_POSITION,
        payload
    }
}
