import { PLACE_BEAD, SET_BRUSH_POSITION, UNDO, REDO } from '../constants/constants'

export function placeBead(payload){
    return {
        type: PLACE_BEAD,
        payload
    }
}

export function setBrushPosition(payload){
    return {
        type: SET_BRUSH_POSITION,
        payload
    }
}

export function undo(payload){
    return {
        type: UNDO,
        payload
    }
}

export function redo(payload){
    return {
        type: REDO,
        payload
    }
}
