import React, { Component } from 'react';
import './App.css';
import {connect} from 'react-redux';
import Canvas from './Canvas.js';
import { undo, redo, setActiveColor } from '../actions/rootActions';
import { SketchPicker } from 'react-color';


function mapStateToProps(state) {
  return {
      beads: state.beads,
      brushPosition: state.brushPosition,
      activeColor: state.activeColor
  };
};

function mapDispatchToProps(dispatch) {
  return {
      undo: () => dispatch(undo()),
      redo: () => dispatch(redo()),
      setActiveColor: (color) => dispatch(setActiveColor(color))
  }
}

class ConnectedApp extends Component {


  constructor(props){
    super(props);
    this.changeColor = this.changeColor.bind(this);
  }

  changeColor(newColor){
    this.props.setActiveColor(newColor.hex);
  }

  render(){
    return (
      <div className="App">
        <Canvas/>
        <SketchPicker className="SketchPicker" onChangeComplete={this.changeColor}></SketchPicker>
      </div>
    );
  }

}
const App = connect(null, mapDispatchToProps)(ConnectedApp);

export default App;
