import React, { Component } from 'react';
import './Canvas.css';
import {connect} from 'react-redux';
import PenDraw from 'react-pen-draw';

function placeObject(){
  console.log();
}

class ConnectedCanvas extends Component {

  constructor(props) {
    super(props);

    this.state = { x: 0, y: 0 };
  }

  _onMouseMove(e) {
    this.setState({ x: e.screenX, y: e.screenY });
  }

  render(){
    return (
      <div className="Canvas" onClick={placeObject}>
              <h1>Mouse coordinates: { x } { y }</h1>
      </div>
    );
  }

}
const Canvas = connect(null)(ConnectedCanvas);

export default Canvas;
