import React, { Component } from 'react';
import './Canvas.css';
import {connect} from 'react-redux';
import Bead from './Bead';
import { placeBead } from '../actions/rootActions';

function mapStateToProps(state) {
  return {
      beads: state.beads
  };
};

function mapDispatchToProps(dispatch) {
  return {
      placeBead: (beads) => dispatch(placeBead(beads))
  }
}

class ConnectedCanvas extends Component {

  constructor(props) {
    super(props);

    this.state = { x: 0, y: 0};
    this.placeObject = this.placeObject.bind(this);
    this._onMouseMove = this._onMouseMove.bind(this)
  }

  placeObject(){
    var beads = this.props.beads;
    var size = 10;
    var x = this.state.x - (size);
    var y = this.state.y - (size);
    console.log("place bead: " + beads.length);
    beads.push(
      <Bead x={x} y={y} width={size} height={size}/>
    );
    this.props.placeBead(beads);
  }

  _onMouseMove(e) {

    this.setState({ x: e.clientX, y: e.clientY });
  }

  render(){
    console.log("render");
    return (
      <div 
        className="Canvas" id="canvas"
        onMouseMove={this._onMouseMove} 
        onClick={this.placeObject}
      >
        {this.props.beads}
      </div>
    );
  }

}
const Canvas = connect(mapStateToProps, mapDispatchToProps)(ConnectedCanvas);

export default Canvas;
