import React, { Component } from 'react';
import './Canvas.css';
import {connect} from 'react-redux';
import Bead from './Bead';
import { placeBead } from '../actions/rootActions';

function mapStateToProps(state) {
  return {
      beads: state.beads
  };
};

function mapDispatchToProps(dispatch) {
  return {
      placeBead: (beads) => dispatch(placeBead(beads))
  }
}

class ConnectedCanvas extends Component {

  constructor(props) {
    super(props);

    this.state = { x: 0, y: 0};
    this.placeObject = this.placeObject.bind(this);
  }

  placeObject(){
    console.log(this.state.x);
    var beads = this.props.beads;
    beads.push(
      <Bead x={this.state.x} y={this.state.y}/>
    )
  }

  _onMouseMove(e) {

    this.setState({ x: e.screenX, y: e.screenY });
  }

  render(){
    console.log(this.props.beads);
    const { x, y } = this.state;
    return (
      <div 
        className="Canvas" id="canvas"
        onMouseMove={this._onMouseMove.bind(this)} 
        onClick={this.placeObject}
      >
        {this.props.beads}
      </div>
    );
  }

}
const Canvas = connect(mapStateToProps, mapDispatchToProps)(ConnectedCanvas);

export default Canvas;
