import React, { Component } from 'react';

class Bead extends Component {

  constructor(props){
    super(props);
    this.state = { mouseHeld: false };
    this.reColor = this.reColor.bind(this);
    this.mouseUp = this.mouseUp.bind(this);
    this.mouseDown = this.mouseDown.bind(this);
  }

  reColor(e){
    console.log(e)
  }

  mouseDown(){
    this.setState({mouseHeld: true});
  }

  mouseUp(){
    this.setState({mouseHeld: false});
  }

  render() {

    return (
      <div 
        className="object"
        style={{
          left: this.props.x, 
          top: this.props.y,
          width: this.props.width,
          height: this.props.height,
          borderRadius: 100,
          backgroundColor: this.props.color,
          position: "absolute",
          padding: 0,
          margin: 0
        }}
        onMouseOver={this.reColor}
      >

      </div>
    )
  }
}

export default Bead;
