import React, { Component } from 'react';
import { connect } from 'react-redux';

function mapStateToProps(state) {
  return {
      activeColor: state.activeColor
  };
};

class ConnectedBead extends Component {

  constructor(props){
    super(props);
    this.state = { mouseHeld: false, color: this.props.color };
    this.reColor = this.reColor.bind(this);
    this.mouseUp = this.mouseUp.bind(this);
    this.mouseDown = this.mouseDown.bind(this);
  }

  reColor(e){
    console.log(this.state);
    if(this.state.mouseHeld && this.props.activeColor != this.props.color){
      console.log("reColor: " + this.props.activeColor)

      this.setState({color: this.props.activeColor})
    }
  }

  mouseDown(){
    this.setState({mouseHeld: true});
  }

  mouseUp(){
    this.setState({mouseHeld: false});
  }

  render() {
    const color = this.state.color;
    return (
      <div 
        className="object"
        style={{
          left: this.props.x, 
          top: this.props.y,
          width: this.props.width,
          height: this.props.height,
          borderRadius: 100,
          backgroundColor: this.state.color,
          position: "absolute",
          padding: 0,
          margin: 0
        }}
        onClick={this.reColor}
        onMouseOver={this.reColor}
        onMouseDown={this.mouseDown}
        onMouseUp={this.mouseUp}
      >

      </div>
    )
  }
}

const Bead = connect(mapStateToProps)(ConnectedBead);

export default Bead;
