import React from 'react';
import './Canvas.css';
import {connect} from 'react-redux';
import PenDraw from 'react-pen-draw';
function ConnectedCanvas() {
  state={
    clear:0
  }
  return (
    <div className="Canvas">
      <PenDraw 
          clearScreen={this.state.clear}
          lineWidth={2}
          lineColor="#000"
          style={{
            border:"1px solid",
            width:"400px",
            height:"400px"
          }}
        />
    </div>
  );
}
const Canvas = connect(null)(ConnectedCanvas);

export default Canvas;
