import React, { Component } from 'react';
import './Canvas.css';
import {connect} from 'react-redux';
import Bead from './Bead';
import { placeBead, setBrushPosition } from '../actions/rootActions';

function mapStateToProps(state) {
  return {
      beads: state.beads,
      brushPosition: state.brushPosition
  };
};

function mapDispatchToProps(dispatch) {
  return {
      placeBead: (beads) => dispatch(placeBead(beads)),
      setBrushPosition: (xy) => dispatch(setBrushPosition(xy))
  }
}

class ConnectedCanvas extends Component {

  constructor(props) {
    super(props);
    this.intervalId = 0;
    this.state = { intervalId: 0};
    this.placeObject = this.placeObject.bind(this);
    this._onMouseMove = this._onMouseMove.bind(this);
    this.snap = this.snap.bind(this);
  }

  snap(numToRound, multiple)
  {
    if (multiple == 0)
        return numToRound;

    var remainder = numToRound % multiple;
    if (remainder == 0)
        return numToRound;

    return numToRound + multiple - remainder;
  }

  brush(){
    var size = 10;
    var x = this.props.brushPosition.x;
    var y = this.props.brushPosition.y;

    // x = this.snap(x, 10);
    // y = this.snap(y, 10);
    return (<Bead x={x} y={y} width={size} height={size}/>);
  }

  mouseDown(){
    this.intervalId = setInterval(this.placeObject, 500);
  }

  mouseUp(){
    clearInterval(this.state.intervalId);
  }

  placeObject(){
    var beads = this.props.beads.slice();
    var size = 10;
    console.log(this.props.brushPosition);
    var x = this.props.brushPosition.x;
    var y = this.props.brushPosition.y;
    // x = this.snap(x, 10);
    // y = this.snap(y, 10);
    console.log("place bead: " + beads.length);
    beads.push(
      <Bead x={x} y={y} width={size} height={size}/>
    );
    this.props.placeBead(beads);
  }

  _onMouseMove(e) {
    var size=10;
    var x = e.clientX - size;
    var y = e.clientY - size;

    x = this.snap(x, 10);
    y = this.snap(y, 10);
    this.props.setBrushPosition({ x: x, y: y });
  }

  render(){
    console.log("render");
    return (
      <div 
        className="Canvas" id="canvas"
        onMouseMove={this._onMouseMove} 
        onMouseDown={this.mouseDown}
        onMouseUp={this.mouseUp}
      >
        {this.props.beads}
        {this.brush()}
      </div>
    );
  }

}
const Canvas = connect(mapStateToProps, mapDispatchToProps)(ConnectedCanvas);

export default Canvas;
