import { PLACE_BEAD, SET_BRUSH_POSITION, REDO, UNDO, ADD_STATE } from "../constants/constants";
import { act } from "react-dom/test-utils";


const initialState = {
    beads: {},
    brushPosition: {x: 0, y: 0},
    history: [],
    histNum: 0,
    activeColor: "#000000"
    // transactions: [],
    // totalCash: 0
};

const rootReducer = (state = initialState, action) => {
    let newState = state;
    switch(action.type) {
        case PLACE_BEAD:

            newState = {
                ...state,
                beads: action.payload
            };

            const history = state.history;
            var histNum = state.histNum;
            history.splice(histNum, 0, newState);
            history.join();
            histNum += 1;

            newState = {
                ...newState,
                history: history,
                histNum: histNum
            }
            break;
        case SET_BRUSH_POSITION:
            newState ={
                ...state,
                brushPosition: action.payload
            };
            break;
        case REDO:
            if(state.histNum < state.history.length-1){
                var histNum = state.histNum;
                histNum = histNum + 1;
                newState = state.history[histNum];
                newState = {
                    ...newState,
                    histNum: histNum
                }
            }
            break;
        case UNDO:
            if(state.histNum > 0){
                var histNum = state.histNum;
                histNum = histNum - 1;
                newState = state.history[histNum];
                newState = {
                    ...newState,
                    histNum: histNum
                }
            }
            break;
        default:
            break;
            // return state;
    }

    return newState;
}
export default rootReducer;
