import { PLACE_BEAD, SET_BRUSH_POSITION } from "../constants/constants";


const initialState = {
    beads: {},
    brushPosition: {x: 0, y: 0},
    history: []
    // transactions: [],
    // totalCash: 0
};

const rootReducer = (state = initialState, action) => {
    switch(action.type) {
        case PLACE_BEAD:
            return {
                ...state,
                beads: action.payload
            }
        case SET_BRUSH_POSITION:
            return{
                ...state,
                brushPosition: action.payload
